const KEBAB_RE = /-([a-z])/g;
const FIRST_LETTER_RE = /^(.)(.*)$/g;

const toTitleCase = (name) =>
  name
    .replace(KEBAB_RE, (s) => s[1].toUpperCase())
    .replace(FIRST_LETTER_RE, (s) => `${s[0].toUpperCase()}${s.slice(1)}`);

const entityToUnicode = (entity) => `\\${entity.slice(3, -1)}`;

const icon = Object.keys(iconData).reduce((obj, key) => {
  obj[`Icon${toTitleCase(key)}`] = {
    "&:before": {
      content: `"${entityToUnicode(iconData[key])}"`
    }
  };
  return obj;
}, {});

export default icon;
